# 给视频添加字幕
def add_captions(src_mp4: str, dsf_mp4: str):
    video = AudioFileClip(src_mp4)
    split = src_mp4.split('.')
    src_wav = split[0] + '.wav'
    video.write_audiofile(src_wav)

    # 转换成文字
    sentences = audio_convert_word(src_wav)

    if len(sentences) < 1:
        print("识别出错")
        return
    all_txt = []
    # 字幕居中
    for sentence, start, end in sentences:
        txt = (TextClip(sentence,
                        font='SimHei',
                        color='white')
               .set_position((0.1, 0.7), relative=True)
               .set_start(start)
               .set_end(end))
        all_txt.append(txt)
    video = CompositeVideoClip([video, *all_txt])
    video.write_videofile(dsf_mp4)


# 音频文件转文字
def audio_convert_word(src_wav: str):
    sentences = []
    r = sr.Recognizer()
    with sr.WavFile(src_wav) as source:  # 请把引号内改成你自己的音频文件路径
        audio = r.record(source)
        result = r.recognize_ibm(audio, username="apikey", password=IBM_api_key, language='en-US')
        if "results" not in result or len(result["results"]) < 1 or "alternatives" not in result["results"][0]:
            print('error:获取解析返回值错误')
        else:
            for result in result["results"]:
                alternatives = result["alternatives"][0]
                transcript = alternatives["transcript"]
                timestamps = alternatives["timestamps"]
                start = timestamps[0][1]
                end = timestamps[len(timestamps) - 1][2]
                sentences.append((transcript, start, end))
    return sentences


